import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Upload, Avatar, message } from 'antd';
import { UserOutlined, UploadOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AdministracionDeCuenta  = () => {

  const navigate = useNavigate();

  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const [userData, setUserData] = useState({});
  const [profileImage, setProfileImage] = useState(null);

  useEffect(() => {
    const Id = localStorage.getItem('Id_Usuario');

    // Fetch user data from backend
    axios.get(`localhost:8000/Ajustadores/ConsultarAjustadoresPorId/${encodeURIComponent(Id)}`)
      .then(response => {
        setUserData(response.data);
        setProfileImage(response.data.profileImage);
        form.setFieldsValue(response.data);
      })
      .catch(error => {
        message.error('Failed to load user data');
      });
  }, [form]);

  const handleProfileImageChange = info => {
    if (info.file.status === 'done') {
      setProfileImage(info.file.response.url);
      message.success('Profile image updated successfully');
    } else if (info.file.status === 'error') {
      message.error('Failed to update profile image');
    }
  };

  const handleFinish = values => {
    setLoading(true);
    axios.post('/api/user/profile', values)
      .then(response => {
        message.success('Profile updated successfully');
      })
      .catch(error => {
        message.error('Failed to update profile');
      })
      .finally(() => {
        setLoading(false);
      });
  };

  //funcion para el boton de cierre de sesion
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/Login');
  };

  return (
    <div style={{ maxWidth: 600, margin: '0 auto', padding: '20px' }}>
      <h1>My Profile</h1>
      <Form
        form={form}
        layout="vertical"
        onFinish={handleFinish}
      >
        <Form.Item label="Profile Image">
          <Avatar size={100} src={profileImage} icon={<UserOutlined />} />
          <Upload
            name="profileImage"
            action="/api/user/upload-profile-image"
            showUploadList={false}
            onChange={handleProfileImageChange}
          >
            <Button icon={<UploadOutlined />} style={{ marginTop: 16 }}>
              Change Profile Image
            </Button>
          </Upload>
        </Form.Item>
        <Form.Item
          name="name"
          label="Name"
          rules={[{ required: true, message: 'Please input your name!' }]}
        >
          <Input placeholder="Enter your name" />
        </Form.Item>
        <Form.Item
          name="email"
          label="Email"
          rules={[{ required: true, message: 'Please input your email!' }]}
        >
          <Input placeholder="Enter your email" />
        </Form.Item>
        <Form.Item>
          <Button type="primary" htmlType="submit" loading={loading}>
            Save Changes
          </Button>
        </Form.Item>
        <Form.Item>
                    <Button type="primary" onClick={handleLogout}>
              Cerrar Sesi√≥n
            </Button>
            </Form.Item>
      </Form>
    </div>
  );
};

export default AdministracionDeCuenta;
