import React, { useState, useEffect } from 'react';
import { Table, message, Modal, Button } from 'antd';
import axios from 'axios';

const DataQuery = () => {
  const token = localStorage.getItem('token');
  const [tableData, setTableData] = useState([]);
  const [id, setIdAjustador] = useState([]);

  const fetchUsersData = async () => {
    try {
      const response = await axios.get('http://localhost:8000/Ajustadores/ConsultarAjustadores', {
        headers: {
          'Authorization': token,
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.status !== 200) {
        throw new Error('Error en la solicitud.');
      }

      const tableData = response.data;

      if (!Array.isArray(tableData)) {
        throw new Error('Los datos recibidos no son un array válido.');
      }

      setTableData(tableData);
    } catch (error) {
      console.error('Error:', error.message);
      message.error('Error al consultar los datos.');
      setTableData([]);
    }
  };

  useEffect(() => {
    fetchUsersData();
  }, [token]);

  const handleDelete = (id) => {
    Modal.confirm({
      title: 'Confirmación de Eliminación',
      content: '¿Estás seguro de que deseas eliminar a este usuario?',
      okText: 'Sí',
      okType: 'danger',
      cancelText: 'No',
      onOk: async () => {
        try {
          const response = await axios.delete(`http://localhost:8000/Ajustadores/EliminarAjustadores/${id}`, {
            headers: {
              'Authorization': token,
              'Content-Type': 'multipart/form-data',
            },
          });
          setIdAjustador(response.Id_Usuario)

          if (response.status !== 200) {
            throw new Error('Error en la solicitud.');
          }

          message.success('Registro eliminado correctamente.');
          fetchUsersData(); // Refresh the data after deletion
        } catch (error) {
          console.error('Error:', error.message);
          message.error('Error al eliminar el registro.');
        }
      },
    });
  };

  const columns = [
    {
      title: 'Nombre',
      dataIndex: 'Nombres',
      key: 'Nombres',
    },
    {
      title: 'Apellidos',
      dataIndex: 'Apellidos',
      key: 'Apellidos',
    },
    {
      title: 'Foto_Perfil',
      dataIndex: 'Foto_Perfil',
      key: 'Foto_Perfil',
      render: (imagen) => (
        <img src={`http://localhost:8000/images/${imagen}`} alt="Imagen de perfil" style={{ width: 100 }} />
      ),
    },
    {
      title: 'Acciones',
      key: 'actions',
      render: (text, record) => (
        <Button
          type="danger"
          onClick={() => handleDelete(record.id)}
          style={{ backgroundColor: 'red' }}
        >
          Eliminar
        </Button>
      ),
    },
  ];

  return (
    <div style={{ padding: '20px' }}>
      <Table columns={columns} dataSource={tableData} />
    </div>
  );
};

export default DataQuery;
