import React, { useState, useEffect } from 'react';
import { Table, message, notification, Button} from 'antd';
import axios from 'axios';

const DataQuery = () => {

  const token = localStorage.getItem('token');

  const [tableData, setTableData] = useState([]);

  const fetchUsersData = async () => {
    try {
      const response = await axios.get('http://localhost:8000/Ajustadores/ConsultarAjustadores',{
        headers: {
          'Authorization': token,
          'Content-Type': 'multipart/form-data',
      }
      });

      if (response.status !== 200) {
        throw new Error('Error en la solicitud.');
      }

      const data = response.data;

      if (!Array.isArray(data)) {
        throw new Error('Los datos recibidos no son un array válido.');
      }

      setTableData(data);
    } catch (error) {
      console.error('Error:', error.message);
      message.error('Error al consultar los datos.');
      setTableData([]);
    }
  };

  useEffect(() => {
    fetchUsersData();
  }, [token]);

  const handleDelete = (id) => {
    confirm({
      title: '¿Estás seguro de que deseas eliminar este registro?',
      okText: 'Sí',
      cancelText: 'No',
      onOk() {
        axios.delete(`/EliminarAjustadores/${id}`)
          .then(response => {
            notification.success({
              message: 'Éxito',
              description: 'Registro eliminado exitosamente',
            });
            // Actualiza el estado para reflejar el cambio en la tabla
            setData(prevData => prevData.filter(item => item.id !== id));
          })
          .catch(error => {
            notification.error({
              message: 'Error',
              description: 'No se pudo eliminar el registro',
            });
          });
      },
    });
  };
  const columns = [
    {
      title: 'Nombre',
      dataIndex: 'Nombres',
      key: 'Nombres',
    },
    {
      title: 'Apellidos',
      dataIndex: 'Apellidos',
      key: 'Apellidos',
    },
    {
      title: 'Foto_Perfil',
      dataIndex: 'Foto_Perfil',
      key: 'Foto_Perfil',
      render: (imagen) => (
        <img src={`http://localhost:8000/images/${imagen}`} alt="Imagen de perfil" style={{ width: 100 }} />
      ),
    },
    {
      title: 'Acciones',
      key: 'actions',
      render: (text, record) => (
        <Button
          type="danger"
          onClick={() => handleDelete(record.id)}
        >
          Eliminar
        </Button>
      ),
    },
  ];

  return (
    <div style={{ padding: '20px' }}>
      <Table columns={columns} dataSource={tableData} />
    </div>
  );
};

export default DataQuery;
