import React, { useState } from 'react';
import { Form, Input, Button, DatePicker, Upload, message } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import axios from 'axios';
import '../../Styles/RegistrarKilometraje.css'; // Archivo de estilo CSS

const RegistrarKilometraje = () => {
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);

  const onFinish = (values) => {
    setLoading(true);
    const formData = new FormData();
    formData.append('KilometrajeRegistrado', values.KilometrajeRegistrado);
    formData.append('FechaDeRegistro', values.FechaDeRegistro.format('YYYY-MM-DD'));
    formData.append('Foto_Evidencia', values.Foto_Evidencia.file.originFileObj);
    formData.append('AjustadorQueRegistra', values.AjustadorQueRegistra);

    axios.post('http://localhost:8000/api/kilometrajes', formData)
      .then(response => {
        message.success('Kilometraje registrado exitosamente');
        form.resetFields();
      })
      .catch(error => {
        message.error('Error al registrar el kilometraje');
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <div
      style={{
        position: 'relative',
        minHeight: '100vh',
        backgroundColor: '#21CEB3',
        borderRadius: '20px',
        overflow: 'hidden', // Asegura que el contenido no se desborde del contenedor
      }}
    >
      <div className="circle-background" style={{marginRight: '4rem', marginTop: '-200px'}}></div>
      <h1>Registro de kilometrajes</h1>
      <img src="https://cdn-icons-png.flaticon.com/128/1455/1455330.png"></img>
      <div style={{ padding: '20px', textAlign: 'center' }}>
      <Form
        form={form}
        onFinish={onFinish}
        layout="vertical"
      >
        <Form.Item
          name="KilometrajeRegistrado"
          label="Kilometraje Registrado"
          rules={[{ required: true, message: 'Por favor ingrese el kilometraje' }]}
        >
          <Input type="number" placeholder="Ingrese el kilometraje" />
        </Form.Item>

        <Form.Item
          name="FechaDeRegistro"
          label="Fecha de Registro"
          rules={[{ required: true, message: 'Por favor seleccione la fecha' }]}
        >
          <DatePicker style={{ width: '100%' }} />
        </Form.Item>

        <Form.Item
          name="Foto_Evidencia"
          label="Foto Evidencia"
          rules={[{ required: true, message: 'Por favor suba la foto evidencia' }]}
          valuePropName="file"
          getValueFromEvent={(e) => e.fileList[0]}
        >
          <Upload beforeUpload={() => false}>
            <Button icon={<UploadOutlined />}>Subir Foto</Button>
          </Upload>
        </Form.Item>

        <Form.Item
          name="AjustadorQueRegistra"
          label="Ajustador que Registra"
          rules={[{ required: true, message: 'Por favor ingrese el nombre del ajustador' }]}
        >
          <Input placeholder="Ingrese el nombre del ajustador" />
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" loading={loading} style={{ width: '100%' }}>
            Registrar Kilometraje
          </Button>
        </Form.Item>
      </Form>
      </div>
    </div>
  );
};

export default RegistrarKilometraje;
