import React, { useState } from 'react';
import { Form, Input, Button, DatePicker, Upload, message } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import axios from 'axios';
import '../../Styles/RegistrarKilometraje.css'; // Archivo de estilo CSS

const RegistrarKilometraje = () => {
  const [form] = Form.useForm();

  const onFinish = (values) => {
    const nombres = localStorage.getItem('nombre');
    
    if (!nombres) {
      message.error('El nombre del ajustador no está disponible');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('KilometrajeRegistrado', values.KilometrajeRegistrado);
    formData.append('FechaDeRegistro', values.FechaDeRegistro.format('YYYY-MM-DD'));
    formData.append('Foto_Evidencia', values.Foto_Evidencia.file.originFileObj);
    formData.append('AjustadorQueRegistra', nombres);

    console.log('Enviando datos del formulario:', formData);

    axios.post('http://localhost:8000/Kilometrajes/RegistrarKilometraje', formData)
      .then(response => {
        message.success('Kilometraje registrado exitosamente');
        form.resetFields();
      })
      .catch(error => {
        console.error('Error al registrar el kilometraje:', error);
        message.error('Error al registrar el kilometraje');
      })
      .finally(() => {
        setLoading(false);
      });
  };

  const normFile = (e) => {
    console.log('Upload event:', e);
    if (Array.isArray(e)) {
      return e;
    }
    return e?.fileList;
  };

  return (
    <div className="container">
      <div className="circle-background" style={{ marginRight: '4rem', marginTop: '-30px' }}></div>
      <h1 className="title">Registro de kilometrajes</h1>
      <img className="icon" src="https://cdn-icons-png.flaticon.com/128/1455/1455330.png" alt="Kilometraje Icon" />
      <div className="form-container">
        <Form
          form={form}
          onFinish={onFinish}
          layout="vertical"
        >
          <Form.Item
            name="KilometrajeRegistrado"
            label="Kilometraje Registrado"
            rules={[{ required: true, message: 'Por favor ingrese el kilometraje' }]}
          >
            <Input type="number" placeholder="Ingrese el kilometraje" />
          </Form.Item>

          <Form.Item
            name="FechaDeRegistro"
            label="Fecha de Registro"
            rules={[{ required: true, message: 'Por favor seleccione la fecha' }]}
          >
            <DatePicker style={{ width: '100%' }} />
          </Form.Item>

          <Form.Item
            name="Foto_Evidencia"
            label="Foto Evidencia"
            rules={[{ required: true, message: 'Por favor suba la foto evidencia' }]}
            valuePropName="fileList"
            getValueFromEvent={normFile}
          >
            <Upload beforeUpload={() => false}>
              <Button icon={<UploadOutlined />}>Subir Foto</Button>
            </Upload>
          </Form.Item>

          <Form.Item>
            <Button type="primary" htmlType="submit" style={{ width: '100%' }}>
              Registrar Kilometraje
            </Button>
          </Form.Item>
        </Form>
      </div>
    </div>
  );
};

export default RegistrarKilometraje;
