import React, { useState } from 'react';
import { Form, Input, Button, message, Row, Col, Select } from 'antd';
import axios from 'axios';

const { Option } = Select;

const AcuerdosConTerceros = ({ Id_SiniestroApasar }) => {
    const [loading, setLoading] = useState(false);
    const [otroAcuerdo, setOtroAcuerdo] = useState(null);
    const [ArriboPrimero, setArriboPrimero] = useState(null);

    const [currentStep, setCurrentStep] = useState(1);

    const nombres = localStorage.getItem('nombre');
    const token = localStorage.getItem('token');

    const onFinish = async (values) => {
        console.log("token que se tiene: " + token);
        setLoading(true);
        try {
            values.Id_Siniestro = Id_SiniestroApasar;
            values.AjustadorQueRegistra = nombres;

            if (otroAcuerdo === 'No') {
                values.AseguradoraDeTercero = 'No aplica';
                values.IndicadorDeArribo = 'No aplica';
                if(ArriboPrimero === 'No'){
                    values.TiempoDeEspera = 'No aplica';
                }
            }

            const jsonData = JSON.stringify(values); // Serializar los datos como JSON
            console.log("Datos a enviar: " + jsonData);
            const response = await axios.post('http://localhost:8000/TercerosInvolucradosSiniestrosNormales/RegistrarTercerosInvolucrados', jsonData, {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': token,
                }
            });

            if (response.status === 200) {
                message.success('Acuerdo registrado exitosamente');
                setCurrentStep(2);  // Avanzar al siguiente paso
            } else {
                message.error('Error al registrar el siniestro');
            }
        } catch (error) {
            message.error('Error al registrar el siniestro');
        }
        setLoading(false);
    };

    const handleOtroAcuerdoChange = (value) => {
        setOtroAcuerdo(value);
    };

    const handleArrioPrimeroChange = (value) => {
        setArriboPrimero(value);
    };

    return (
        <div style={{borderRadius: '8px', textAlign: 'center', width: '102%'}}>
            {currentStep === 1 && (
                <>
                    <h1 className="title">Acuerdos con Terceros involucrados</h1>
                    <div style={{ textAlign: 'center', display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100%', marginTop: '2rem', marginBottom: '2rem' }}>
                        <img src="https://cdn-icons-png.flaticon.com/512/1604/1604716.png" style={{ maxHeight: '150px' }} alt="Gastos.jpg" />
                    </div>
                    <div className="circle-background" style={{ position: 'absolute', marginTop: '-90px', marginLeft: '-25px' }}></div>
                    <Form layout="vertical" onFinish={onFinish}>
                        <Row gutter={16}>
                            <Col xs={24} md={12}>
                                <Form.Item
                                    name="Resultado"
                                    label="Seleccione el resultado al que se llego con el tercero"
                                    rules={[{ required: true, message: 'Por favor seleccione una opción' }]}
                                >
                                    <Select onChange={handleOtroAcuerdoChange}>
                                        <Option value="Si">Entregue pase medico</Option>
                                        <Option value="No">No</Option>
                                        <Option value="No">No</Option>
                                        <Option value="No">No</Option>
                                        <Option value="No">No</Option>
                                        <Option value="No">No</Option>
                                    </Select>
                                </Form.Item>
                            </Col>
                        </Row>
                        {otroAcuerdo === 'Si' && (
                            <>
                                <Row gutter={16}>
                                    <Col xs={24} md={12}>
                                        <Form.Item
                                            name="AseguradoraDeTercero"
                                            label="¿Cuál es su compañía aseguradora?"
                                            rules={[{ required: true, message: 'Por favor seleccione una opción' }]}
                                        >
                                            <Select>
                                                <Option value="Compañía A">Compañía A</Option>
                                                <Option value="Compañía B">Compañía B</Option>
                                            </Select>
                                        </Form.Item>
                                    </Col>
                                    <Col xs={24} md={12}>
                                        <Form.Item
                                            name="IndicadorDeArribo"
                                            label="¿Llegué primero?"
                                            rules={[{ required: true, message: 'Por favor seleccione una opción' }]}
                                        >
                                            <Select onChange={handleArrioPrimeroChange}>
                                                <Option value="Si">Si</Option>
                                                <Option value="No">No</Option>
                                            </Select>
                                        </Form.Item>
                                    </Col>
                                </Row>
                                {ArriboPrimero === 'Si' && (
                                    <Row gutter={16}>
                                        <Col xs={24} md={12}>
                                            <Form.Item
                                                name="TiempoDeEspera"
                                                label="¿Cuánto tiempo se esperó a la aseguradora del tercero?"
                                                rules={[{ required: true, message: 'Por favor ingrese el tiempo de espera' }]}
                                            >
                                                <Input />
                                            </Form.Item>
                                        </Col>
                                    </Row>
                                )}
                            </>
                        )}
                        <Row gutter={16}>
                            <Col xs={24}>
                                <Form.Item>
                                    <Button type="primary" htmlType="submit" loading={loading} style={{ width: '100%', boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)' }}>
                                        Registrar acuerdo
                                    </Button>
                                </Form.Item>
                            </Col>
                        </Row>
                    </Form>
                </>
            )}
            {currentStep === 2 && (
                <div>
                    {/* <TercerosSiniestroNormal Id_SiniestroApasar={Id_SiniestroApasar} /> */}
                </div>
            )}
        </div>
    );
};

export default AcuerdosConTerceros;
